(function($) {
    "use strict";
    if(cookie_id == '' || cookie_id == undefined)
    {
        cookie_id = 'phphelpm4cookie';
    }
    if (localStorage.getItem(cookie_id) === undefined || localStorage.getItem(cookie_id) === null){
        setTimeout(function () {
            $('.cookie-box').addClass('active');
        }, 2000);
        $(document).on('click', '.cookie-box .button' , function () {
            var thisButton = $(this),
                Buttontxt = thisButton.html();
            thisButton.html('<i class="fa fa-circle-notch fa-spin fa-fw"></i> '+Buttontxt);
            thisButton.prop('disabled',true);
            if (thisButton.hasClass('cookie-consent')){
                localStorage.setItem(cookie_id, true);
                setTimeout(function () {
                    $('.cookie-box').removeClass('active');
                }, 1000)
            } else {
                
                setTimeout(function () {
                    $('.cookie-box').removeClass('active');
                }, 1000)
            }
            thisButton.prop('disabled', false);
        });
    }
})(jQuery);